$path = Read-Host "Path to files (Press Enter for C:\Program Files\Autodesk\Revit Preview Release)"; if ([string]::IsNullOrWhiteSpace($path)) { $path = "C:\Program Files\Autodesk\Revit Preview Release" }; Push-Location $path; $names = @("AdWindows","PackageContentsParser","RevitAddInUtility","RevitAPI","RevitAPIIFC","RevitAPIMacros","RevitAPIUI","RevitNET","UIFramework","UIFrameworkServices"); $files = $names | ForEach-Object { "$_.dll", "$_.xml" } | Where-Object { Test-Path $_ }; Compress-Archive -Path $files -DestinationPath "$env:USERPROFILE\Desktop\RevitFiles.zip" -Force; Pop-Location; Write-Host "Done! Archive is on Desktop"